import React, { useState } from 'react';
import '../styles/ExerciseLog.css';

function ExerciseLog() {
  const [exercise, setExercise] = useState('');
  const [duration, setDuration] = useState('');
  const [date, setDate] = useState('');
  const [message, setMessage] = useState('');

  const handleLogExercise = async (e) => {
    e.preventDefault();

    const exerciseData = {
      exercise,
      duration,
      date,
    };

    try {
      const response = await fetch('/api/log-exercise', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(exerciseData),
      });

      if (response.ok) {
        setMessage('Exercise log submitted successfully!');
        // Reset form
        setExercise('');
        setDuration('');
        setDate('');
      } else {
        setMessage('Failed to submit exercise log');
      }
    } catch (error) {
      setMessage('An error occurred. Please try again.');
    }
  };

  return (
    <div>
      <h2>Log Exercise</h2>
      {message && <p>{message}</p>}
      <form onSubmit={handleLogExercise}>
        <div>
          <label>Exercise:</label>
          <input 
            type="text" 
            value={exercise} 
            onChange={(e) => setExercise(e.target.value)} 
            required 
          />
        </div>
        <div>
          <label>Duration (in minutes):</label>
          <input 
            type="number" 
            value={duration} 
            onChange={(e) => setDuration(e.target.value)} 
            required 
          />
        </div>
        <div>
          <label>Date:</label>
          <input 
            type="date" 
            value={date} 
            onChange={(e) => setDate(e.target.value)} 
            required 
          />
        </div>
        <button type="submit">Log Exercise</button>
      </form>
    </div>
  );
}

export default ExerciseLog;
