import React, { useState } from 'react';
import '../styles/ExerciseLog.css';

function ExerciseLog() {
  const [exercise, setExercise] = useState('');
  const [duration, setDuration] = useState('');
  const [date, setDate] = useState('');
  const [message, setMessage] = useState('');

  const handleLogExercise = async (e) => {
    e.preventDefault();

    const exerciseData = {
      exercise,
      duration,
      date,
    };

    console.log('Sending exercise data:', exerciseData); // Log the data being sent

    try {
      const response = await fetch('http://localhost:5000/api/log-exercise', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(exerciseData),
      });

      console.log('Response status:', response.status); // Log the response status

      if (response.ok) {
        setMessage('Exercise log submitted successfully!');
        // Reset form
        setExercise('');
        setDuration('');
        setDate('');
      } else {
        const errorMessage = await response.text(); // Get response text for error details
        console.error('Error response:', errorMessage);
        setMessage('Failed to submit exercise log');
      }
    } catch (error) {
      console.error('Fetch error:', error);
      setMessage('An error occurred. Please try again.');
    }
  };

  return (
    <div className="exercise-log-container">
      <h2>Log Exercise</h2>
      {message && <p>{message}</p>}
      <form onSubmit={handleLogExercise}>
        <div>
          <label>Exercise:</label>
          <select 
            value={exercise} 
            onChange={(e) => setExercise(e.target.value)} 
            required
          >
            <option value="">Select an exercise</option>
            <option value="Running">Running</option>
            <option value="Cycling">Cycling</option>
            <option value="Walking">Walking</option>
            <option value="Swimming">Swimming</option>
            <option value="Jumping Rope">Jumping Rope</option>
            <option value="Weightlifting">Weightlifting</option>
            <option value="Bodyweight Exercises">Bodyweight Exercises</option>
            <option value="Yoga">Yoga</option>
            <option value="Pilates">Pilates</option>
            <option value="Stretching">Stretching</option>
            <option value="HIIT">HIIT</option>
            <option value="Soccer">Soccer</option>
            <option value="Basketball">Basketball</option>
            <option value="Tennis">Tennis</option>
          </select>
        </div>
        <div>
          <label>Duration (in minutes):</label>
          <input 
            type="number" 
            value={duration} 
            onChange={(e) => setDuration(e.target.value)} 
            required 
          />
        </div>
        <div>
          <label>Date:</label>
          <input 
            type="date" 
            value={date} 
            onChange={(e) => setDate(e.target.value)} 
            required 
          />
        </div>
        <button type="submit">Log Exercise</button>
      </form>
    </div>
  );
}

export default ExerciseLog;
