import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import '../styles/Goals.css'; // Ensure you import your CSS styles

function Goals() {
  const [goal, setGoal] = useState('');
  const [deadline, setDeadline] = useState('');
  const [goalsList, setGoalsList] = useState([]);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const { user } = useAuth(); // Accessing user context for authentication

  const handleSetGoal = async (e) => {
    e.preventDefault();
    setError(''); // Reset error state
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5000/api/goals', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ goal, deadline }),
      });

      const data = await response.json();
      if (response.ok) {
        setGoalsList((prevGoals) => [...prevGoals, data.goal]); // Update the goals list
        setGoal(''); // Clear input fields
        setDeadline('');
        console.log('Goal submitted:', { goal, deadline });
      } else {
        setError(data.msg || 'Error submitting goal');
        console.error('Error submitting goal:', data.msg);
      }
    } catch (error) {
      setError('An error occurred. Please try again.');
      console.error('Error:', error);
    }
  };

  // Fetch existing goals when the component mounts
  useEffect(() => {
    const fetchGoals = async () => {
      setLoading(true);
      try {
        const token = localStorage.getItem('token');
        const response = await fetch('http://localhost:5000/api/goals', {
          method: 'GET',
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        const data = await response.json();
        if (response.ok) {
          setGoalsList(data.goals); // Assuming the API returns a list of goals
        } else {
          setError(data.msg || 'Error fetching goals');
          console.error('Error fetching goals:', data.msg);
        }
      } catch (error) {
        setError('An error occurred. Please try again.');
        console.error('Error:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchGoals();
  }, [user]); // Refetch goals if user changes

  return (
    <div>
      <h2>Set Fitness Goals</h2>
      <form onSubmit={handleSetGoal}>
        <div>
          <label>Goal:</label>
          <input 
            type="text" 
            value={goal} 
            onChange={(e) => setGoal(e.target.value)} 
            required 
          />
        </div>
        <div>
          <label>Deadline:</label>
          <input 
            type="date" 
            value={deadline} 
            onChange={(e) => setDeadline(e.target.value)} 
            required 
          />
        </div>
        <button type="submit">Set Goal</button>
      </form>
      {error && <p className="error">{error}</p>} {/* Display error message */}
      {loading && <p>Loading...</p>} {/* Show loading state */}
      <h3>Your Goals</h3>
      <ul>
        {goalsList.map((g, index) => (
          <li key={index}>
            {g.goal} - {g.deadline}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Goals;
